css:
body {
  font-family: Arial, sans-serif;
  font-size: 13px;
  margin: 0;
  padding: 0;
}

/*<!-- Proforma Invoice Heading -->*/
.heading-box {
  width: 100%;
  border: 1px solid #000;
  border-collapse: collapse;
  margin-bottom: 2px;
}

.heading-box td {
  text-align: center;
  font-size: 16px;
  font-weight: bold;
  padding: 5px;
}
/*<!-- Consignee and Delivery Box -->*/
  .D {
            font-family: Arial, sans-serif;
            margin: 20px;
            background-color: white;
        }
        .container {
            width: 100%;
            max-width: 1200px;
            margin: 0 auto;
        }
        .main-container {
            border: 1px solid black;
            display: flex;
            height: 400px;
            border-bottom: none;
        }
        .left-section {
            width: 50%;
            border-right: 1px solid black;
            padding: 20px;
        }
        .right-section {
            width: 50%;
            display: flex;
            flex-direction: column;
        }

        /* Left section styling */
        .consignee-label {
            font-size: 16px;
            font-weight: bold;
            margin-bottom: 8px;
        }
        .company-name {
            font-size: 16px;
            font-weight: bold;
            margin-bottom: 5px;
        }
        .address-text {
            font-size: 14px;
            line-height: 1.5;
            margin-bottom: 25px;
        }
        .gstin-text {
            font-size: 14px;
            font-weight: bold;
            margin-bottom: 8px;
        }
        .pan-text {
            font-size: 14px;
            font-weight: bold;
        }

        /* Right section - table area */
        .info-table-container {
            display: flex;
            height: 200px;
        }
        .labels-column {
            width: 50%;
            border-right: 1px solid black;
        }
        .values-column {
            width: 50%;
        }
        .info-row {
            height: 50px;
            border-bottom: 1px solid black;
            display: flex;
            align-items: center;
            padding: 0 15px;
            font-size: 14px;
            font-weight: bold;
        }
        .info-row:last-child {
            border-bottom: none;
        }
        .value-row {
            height: 50px;
            border-bottom: 1px solid black;
            display: flex;
            align-items: center;
            padding: 0 15px;
            font-size: 14px;
        }
        .value-row:last-child {
            border-bottom: none;
        }

        /* Delivery section */
        .delivery-area {
            flex: 1;
            padding: 9px;
            border-top: 1px solid black;
        }
        .delivery-header {
            font-size: 16px;
            font-weight: bold;
            margin-bottom: 5px;
            margin-top:20px;
        }
        .delivery-company {
            font-size: 16px;
            font-weight: bold;
            margin-bottom: 5px;
        }
            .delivery-address {
            font-size: 14px;
            line-height: 1.5;
        }
.inner-table {
  width: 100%;
  border-collapse: collapse;
}

.inner-table td {
  border: 1px solid black;
  padding: 5px 8px;
  vertical-align: top;
}

.invoice-section {
  font-family: Arial, sans-serif;
  font-size: 14px;

}

.item-table {
  width: 100%;
  border-collapse: collapse;
  margin-bottom: 0px;
    border-top: none;
}

.item-table th, .item-table td {
  border: 1px solid black;
  padding: 6px;
  text-align: left;
  border-bottom: none;
}

.item-table th {

  text-align: center;
}


 body {
            font-family: Arial, sans-serif;
            margin: 10px;
            background-color: white;
        }

        .summary-section {
            display: flex;
          border-top: 1px solid black;
              border-bottom: none;
            background-color: white;
            margin: 0;
            padding: 0;
        }

        .left-summary {
            width: 60%;
            border: 1px solid black;
            padding: 10px;
            box-sizing: border-box;
            font-size: 12px;
            border-bottom: 0;
            border-top: none;

        }

        .left-summary p {
            margin: 0 0 10px 0;
            line-height: 1.3;
        }

        .left-summary p:last-child {
            margin-bottom: 0;
        }

        .right-summary {
            width: 40%;
            border: 1px solid black;
            border-left: 0;
            border-bottom: 0;
            border-top: none;

        }

        .summary-table {
            width: 100%;
              border-top: none;
        }

        .summary-table td {
            /*border: 1px solid black;*/
            padding: 4px 6px;
            text-align: right;
            font-size: 12px;
        }

        .summary-table td:first-child {
            text-align: left;
            font-weight: normal;
        }


/*<!-- Terms & Conditions and Remarks -->*/
.payment-terms-box {
  border: 1px solid black;
  font-family: Arial, sans-serif;
  font-size: 12px;
  width: 100%;
  border-collapse: collapse;

}

.terms-table, .remarks-row {
  width: 100%;
  border-collapse: collapse;
}

.terms-table td {
  padding: 6px 8px;
  vertical-align: top;
}

.section-header {
  font-weight: bold;
  padding: 6px 8px;
  border-bottom: 1px solid black;
}

.sr-no {
  width: 30px;
  text-align: left;
  padding-left: 10px;
}

.term-label {
  width: 180px;
  font-weight: bold;
}

.value {
  width: auto;
}
.remarks-row td {
  border-top: 1px solid black;
  padding: 6px 8px;
  font-weight: bold;
  border-bottom: 1px solid black;
}
